mapper.update:
update base_organization_user_role set id = #{id}

	,organization_user_id = #{organizationUserId}


	,organization_role_id = #{organizationRoleId}


	,status = #{status}

 where id = #{id}

mapper.select:
SELECT bour.id organizationUserRoleId, bour.organization_user_id organizationUserId, bour.organization_role_id organizationRoleId, bour.`status`, bou.`name` organizationUserName, boRole.id operationalRoleId, boRole.`name` operationalRoleName, boRole.is_used operationalRoleIsUsed FROM base_organization_user_role bour LEFT JOIN base_organization_user bou ON bou.id = bour.organization_user_id LEFT JOIN base_organization_role bor ON bor.id = bour.organization_role_id LEFT JOIN base_operational_role boRole ON boRole.id = bor.operational_role_id
WHERE bour.`status` = 0 AND boRole.is_used = TRUE AND bor.`status` = 0
and bour.organization_user_id = #{organizationUserId}
and bour.organization_role_id = #{organizationRoleId}
and boRole.`name` like CONCAT(CONCAT('%', #{operationalRoleName}), '%')
