mapper.update:
update base_recommend_organization  set id = #{id}

  ,sort = #{sort}

where id = #{id}

mapper.select:
SELECT bo.id organizationId, bo.`name`, bo.`code`, bo.phone, bo.logo, bo.`status`, bo.organization_type_id organizationTypeId, bot.`name` organizationTypeName, bro.sort, bro.id FROM base_recommend_organization bro INNER JOIN base_organization bo ON bo.id = bro.recommend_organization_id AND bro.organization_id = #{organizationId}
AND bro.organization_site_id = #{bro.organizationSiteId}
LEFT JOIN base_organization_site bos ON bos.organization_id = bo.id LEFT JOIN base_organization_type bot ON bot.id = bo.organization_type_id
WHERE bo.`status` = #{organization.status} AND bo.organization_type_id <13
and bo.`name` like CONCAT(CONCAT('%', #{organization.name}), '%')
GROUP BY bo.id ORDER BY bro.sort DESC

SELECT bo.id, bo.`code`, bo.`name`, bo.phone, bo.`status`, bo.organization_type_id organizationTypeId, bot.`name` organizationTypeName, bos.id siteId FROM base_organization bo LEFT JOIN base_organization_type bot ON bot.id = bo.organization_type_id
LEFT JOIN base_organization_site bos ON bos.organization_id = bo.id WHERE bo.id NOT IN ( SELECT bro.recommend_organization_id FROM base_recommend_organization bro WHERE bro.organization_id = #{organizationId}
AND bro.organization_site_id =#{organizationSiteId}
)
and bo.`code` =#{organization.code}
and bo.`status` =#{organization.status}
and bo.`name` like CONCAT(CONCAT('%', #{organization.name}), '%')
GROUP BY bo.id

SELECT bro.id, bro.organization_id organizationId, bro.organization_site_id organizationSiteId, bro.recommend_organization_id recommendOrganizationId, bro.sort FROM base_recommend_organization bro WHERE bro.organization_id = #{organizationId}
AND bro.organization_site_id =#{organizationSiteId}
AND bro.sort < ( SELECT ro.sort FROM base_recommend_organization ro WHERE ro.id = #{id} ) ORDER BY bro.sort DESC LIMIT 0,1

SELECT bro.id, bro.organization_id organizationId, bro.organization_site_id organizationSiteId, bro.recommend_organization_id recommendOrganizationId, bro.sort FROM base_recommend_organization bro WHERE bro.organization_id = #{organizationId}
AND bro.organization_site_id = #{organizationSiteId}
AND bro.sort > ( SELECT ro.sort FROM base_recommend_organization ro WHERE ro.id = #{id} ) ORDER BY bro.sort ASC LIMIT 0,1
