mapper.select:
SELECT bd.id, bd.`name`, bd.organization_id organizationId, bd.create_date createDate, bo.`name` organizationName, bd.remark FROM base_department bd LEFT JOIN base_organization bo ON bd.organization_id = bo.id
bd.organization_id=#{organizationId}
and bd.name like CONCAT(CONCAT('%', #{name}), '%')

mapper.update:
UPDATE base_department SET id=#{id}

    ,`name` =#{name}


     ,organization_id=#{organizationId}


    ,remark =#{remark}

 WHERE id=#{id}
