mapper.select:
SELECT bou.id, bou.account, bou.birthday, bou.create_date createDate, bou.is_init_password isInitPassword, bou.`name`, bou.phone, bou.phone_status phoneStatus, bou.photo, bou.remark, bou.sex, bou.`status`, bou.organization_person_type_id organizationPersonTypeId, bou.certificate_type certificateType, bou.certificate_type_name certificateTypeName, bou.certificate_number certificateNumber, bou.positive, bou.opposite FROM base_organization_user bou
bou.phone =#{phone}
and bou.account =#{account}

mapper.update:
	UPDATE base_organization_user SET id = #{id}

			,name = #{name}


			, sex = #{sex}


			,birthday = #{birthday}


			,phone = #{phone}


			,photo = #{photo}


			,status = #{status}


			,account = #{account}


			,password = #{password}


			,is_init_password = #{isInitPassword}


			, remark = #{remark}


		    ,organization_person_type_id=#{organizationPersonTypeId}


		    ,certificate_type=#{certificateType}


 		   ,certificate_type_name=#{certificateTypeName}


	        ,certificate_number=#{certificateNumber}


	 		,positive=#{positive}


	 		,opposite=#{opposite}

	WHERE 
		id = #{id}
